    },
    {
      "content": "## Organization",
      "bounding_box": {
        "x": 0.11725490196078431,
        "y": 6.925726975846637,
        "width": 0.13137254901960785,
        "height": 0.020303030303030302,
        "text": "Organization",
        "confidence": 0.7171760093262032,
        "page": 9,
        "normalized": true,
